Genghis.Models.BaseModel = Backbone.Model.extend({
    name: function() {
        return this.get('name');
    },
    count: function() {
        return this.get('count');
    },
    humanCount: function() {
        return Genghis.Util.humanizeCount(this.get('count') || 0);
    },
    isPlural: function() {
        return this.get('count') !== 1;
    },
